import React, { useState } from 'react';
//import { Row, Col } from 'react-bootstrap';
import { Form, Button, InputGroup } from 'react-bootstrap';
import MainCard from '../../ui-component/cards/MainCard';
import axios from 'axios';

const UploadFile = () => {
    const [file, setFile] = useState();

    const handleFileUpload = (event) => {
        if (event.target.files) {
            setFile(event.target.files[0]);
            //console.log(event.target.files[0]);
        }

        //console.log(file);
        //console.log(event.target.files[0].name);
        
    };

    const handleSubmit = async () => {
        const formData = new FormData();
        formData.append("file", {name: file.name, type : file.type});

        console.log(file);
//        formData.append('filename', file.name);
        const config = {
            headers: {
              'content-type': 'multipart/form-data',
            },
        };
        console.log(formData);

        try {
            const response = await axios.post('http://localhost:5000/api/users/uploadExcel', formData, config);
            alert(response.data.msg)
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <MainCard title="upload-excel" isOption>
                        <InputGroup className="mb-3 cust-file-button">
                            <div className="custom-file">
                                <Form.Control
                                    aria-describedby="custom-addons8"
                                    type="file"
                                    className="custom-file-input"
                                    id="validatedCustomFile4"
                                    onChange={handleFileUpload}
                                />
                                <Form.Label className="custom-file-label" htmlFor="validatedCustomFile4">
                                    Choose file
                                </Form.Label>
                            </div>
                            <InputGroup.Append>
                                <Button onClick={handleSubmit} id="custom-addons8">
                                    Upload
                                </Button>
                            </InputGroup.Append>
                        </InputGroup>
                        <div>
                            <text>{file && `${file.name} - ${file.type}`}</text>
                        </div>
            </MainCard>
            
    );
};

export default UploadFile;
